MNIST_NAME=( bloodmnist )

for init in imagenet; do for run in {1..20}; do for task in "${MNIST_NAME[@]}"; do for partial in $(seq 1.00000 0.00005 1.00000) $(seq 0.00100 0.00100 0.00900) $(seq 0.01000 0.01000 0.09000) $(seq 0.10000 0.10000 0.90000); do while true; do if [ $(myjobs | wc -l) -lt 999 ]; then if [ ! -f logs/$task-$run-p$partial.out ]; then sbatch --error=logs/$task-$run-p$partial.out --output=logs/$task-$run-p$partial.out hg.sh $run $task $partial $init; echo "logs/$task-$run-p$partial.out"; break; else line="$(tail -n 1 logs/$task-$run-p$partial.out)"; if [[ "$line" != *"AVERAGE"* ]] && [[ "$line" != *"reraise"* ]] && [[ "$line" != *"Sample larger than population"* ]]; then sbatch --error=logs/$task-$run-p$partial.out --output=logs/$task-$run-p$partial.out hg.sh $run $task $partial $init; echo "logs/$task-$run-p$partial.out"; break; else break; fi; fi; fi; sleep 10s; done; done; done; done; done
